function data = synthesize_data_cells(len)  % Creates a CELLARRAY of chars and doubles to use for training and testing.  % The format of each cell is a1b2c3  %  %  %    % Create alphabets and numeral series.  alphabets = 'a':'z';  numbers = 0:9;    % Choose random indices  char_indices = randperm (length(alphabets), len);  num_indices = randperm (length(numbers), len);    % Pick elements corresponding to the random indices.  char_rand = alphabets (char_indices);  num_rand = numbers (num_indices);    % Create a cell array containing data in the form a1b2c3.  cell_data = cellstr (char_rand(:))';  cell_data = horzcat (cell_data, num2cell(num_rand));    cell_letters = cell_data (1:len);  cell_numbers = cell_data (len + 1: 2*len);    data = [cell_letters; cell_numbers];  data = data(:).';end