function pair = synthesize_data_struct(len)  % Creates a struct with 2 fields: sequence and answer that can be used as  % data for training/testing. Each sequence consists of chars and doubles.  %  % EXAMPLE:   %  % The format of each cell is a1b2c3??c. The '??' separates the query 'c'  % from the rest of the sequence a1b2c3. The answer field will contain 3  % in the above example.  %  %  %    % Create alphabets and numeral series.  alphabets = 'a':'z';  numbers = 0:9;    % Choose random indices  char_indices = randperm (length(alphabets), len);  num_indices = randi (length(numbers), 1, len);  query_idx = randperm(3, 1);  separator = repmat ('?', 1, 2);    % Pick elements corresponding to the random indices.  char_rand = alphabets (char_indices);  num_rand = numbers (num_indices);  query = char_rand (query_idx);  query_ans = num_rand (query_idx);    % Create a cell array containing data in the form a1b2c3.  cell_data = cellstr (char_rand(:))';  cell_data = horzcat (cell_data, num2cell(num_rand));    cell_letters = cell_data (1:len);  cell_numbers = cell_data (len + 1: 2*len);    data = [cell_letters; cell_numbers];  data = data(:).';  data = [data cellstr( separator(:))' cellstr(query(:))];    % Create the struct with sequence and answer fields  pair.sequence = data;  pair.answer = query_ans;  end